// Helper functions for accessing interlinks
export function getVerseInterlinks(textType: 'quran' | 'bible' | 'torah', reference: string): VerseInterlinks | null {
  const key = `${textType}-${reference.toLowerCase().replace(/[:s]/g, '-')}`
  return COMPREHENSIVE_INTERLINKS[key] || null
}

export function getAllInterlinkedVerses(): string[] {
  return Object.keys(COMPREHENSIVE_INTERLINKS)
}

export function getInterlinksByTheme(theme: string): string[] {
  return Object.entries(COMPREHENSIVE_INTERLINKS)
    .filter(([_, interlinks]) => interlinks.themes.includes(theme))
    .map(([key, _]) => key)
}

export function getInterlinksByKeyword(keyword: string): string[] {
  return Object.entries(COMPREHENSIVE_INTERLINKS)
    .filter(([_, interlinks]) => 
      interlinks.keywords.some(k => k.toLowerCase().includes(keyword.toLowerCase()))
    )
    .map(([key, _]) => key)
}

// Statistics
export const INTERLINK_STATS = {
  totalVerses: Object.keys(COMPREHENSIVE_INTERLINKS).length,
  totalRelatedTexts: Object.values(COMPREHENSIVE_INTERLINKS)
    .reduce((sum, interlinks) => sum + interlinks.relatedTexts.length, 0),
  totalCrossReferences: Object.values(COMPREHENSIVE_INTERLINKS)
    .reduce((sum, interlinks) => sum + interlinks.crossReferences.length, 0),
  themes: Array.from(new Set(
    Object.values(COMPREHENSIVE_INTERLINKS)
      .flatMap(interlinks => interlinks.themes)
  )).sort()
}
